<?phpclass Database extends PDO {    private $fetch_mode = PDO::FETCH_OBJ;    function __construct() {        parent::__construct('mysql:dbname=' . DATABASE . ';host=' . DB_HOST, DB_USER, DB_PASS);        $this->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_WARNING);    }         function setFetchMode($fetch_mode) {        $this->fetch_mode = $fetch_mode;    }         function query($sql) {        $stmt = parent::query($sql);        if($stmt) {            $stmt->setFetchMode($this->fetch_mode);        }        return $stmt;    }}?>