<?php////// database.php//class Database extends PDO {    private $fetch_mode = PDO::FETCH_OBJ;         function __construct() {        parent::__construct('mysql:dbname=todolist;host=127.0.0.1', 'root', 'trustnoone');        $this->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_WARNING);        //       $this->setAttribute(PDO::ATTR_STATEMENT_CLASS, array('DBStatement', array($this)));    }         function setFetchMode($fetch_mode) {        $this->fetch_mode = $fetch_mode;    }         function query($sql) {        $stmt = parent::query($sql);        if($stmt) {            $stmt->setFetchMode($this->fetch_mode);        }        return $stmt;    }     }?>